using System;namespace lab1{    abstract class Ski_jumping    {        public string Name = "Прыжки на лыжах"        public void Name()        {            Console.WriteLine(this.Name);        }    }    class Ski_jumping_120 : Ski_jumping    {        public string Surname { get; private set; }        public int Len { get;private set; }        private int[] ScList { get; }        public int Sc { get; private set; }        this.Name = "Прыжки на лыжах со 120 метрового трамплина"        public Res(string Surname, int Len, int[] ScList)        {            this.Surname = Surname;            this.Len = Len;            this.ScList = ScList;            int Sum = 0, Mx = ScList[0], Mn = ScList[0];            for (int i = 0; i < ScList.Length; i++)            {                Sum += ScList[i];                Mx = Math.Max(Mx, ScList[i]);                Mn = Math.Min(Mn, ScList[i]);            }            Sum -= Mx + Mn;            if (Len >= 120)            {                Sum += 60 + (Len - 120) * 2;            }            else            {                Sum -= (120 - Len) * 2;            }            this.Sc = Sum;        }    }    class Ski_jumping_180 : Ski_jumping    {        public string Surname { get; private set; }        public int Len { get;private set; }        private int[] ScList { get; }        public int Sc { get; private set; }        this.Name = "Прыжки на лыжах co 180 метрового трамплина"        public Res(string Surname, int Len, int[] ScList)        {            this.Surname = Surname;            this.Len = Len;            this.ScList = ScList;            int Sum = 0, Mx = ScList[0], Mn = ScList[0];            for (int i = 0; i < ScList.Length; i++)            {                Sum += ScList[i];                Mx = Math.Max(Mx, ScList[i]);                Mn = Math.Min(Mn, ScList[i]);            }            Sum -= Mx + Mn;            if (Len >= 180)            {                Sum += 60 + (Len - 180) * 2;            }            else            {                Sum -= (180 - Len) * 2;            }            this.Sc = Sum;        }    }    class MainClass    {        public static void Main(string[] args)        {            Ski_jumping_120[] m = new Ski_jumping_120[4]; // Иванов Петров Сидоров Пупкин            m[0] = new Ski_jumping_120("Иванов", 123, new int[] { 15, 14, 13, 11, 16 });            m[1] = new Ski_jumping_120("Петров", 111, new int[] { 13, 14, 15, 16, 17 });            m[2] = new Ski_jumping_120("Сидров", 120, new int[] { 16, 19, 20, 18, 17 });            m[3] = new Ski_jumping_120("Пупкин", 119, new int[] { 19, 19, 20, 19, 19 });            Console.WriteLine(m.Name)            for (int i = 0; i < m.Length; i++)            {                Console.WriteLine(i + 1 + ". " + m[i].Surname + ": " + m[i].Sc);            }            for (int i = 0; i < m.Length - 1; i++)            {                double amax = m[i].Sc;                int imax = i;                for (int j = i + 1; j < m.Length; j++)                {                    if (m[j].Sc > amax)                    {                        amax = m[j].Sc;                        imax = j;                    }                }                Ski_jumping_120 temp;                temp = m[imax];                m[imax] = m[i];                m[i] = temp;            }            Console.WriteLine();            for (int i = 0; i < m.Length; i++)            {                Console.WriteLine(i + 1 + ". " + m[i].Surname + ": " + m[i].Sc);            }            Ski_jumping_120[] m = new Ski_jumping_180[4]; // Иванов Петров Сидоров Пупкин            m[0] = new Ski_jumping_180("Иванов", 123, new int[] { 15, 14, 13, 11, 16 });            m[1] = new Ski_jumping_180("Петров", 111, new int[] { 13, 14, 15, 16, 17 });            m[2] = new Ski_jumping_180("Сидров", 120, new int[] { 16, 19, 20, 18, 17 });            m[3] = new Ski_jumping_180("Пупкин", 119, new int[] { 19, 19, 20, 19, 19 });            Console.WriteLine(m.Name)            for (int i = 0; i < m.Length; i++)            {                Console.WriteLine(i + 1 + ". " + m[i].Surname + ": " + m[i].Sc);            }            for (int i = 0; i < m.Length - 1; i++)            {                double amax = m[i].Sc;                int imax = i;                for (int j = i + 1; j < m.Length; j++)                {                    if (m[j].Sc > amax)                    {                        amax = m[j].Sc;                        imax = j;                    }                }                Ski_jumping_180 temp;                temp = m[imax];                m[imax] = m[i];                m[i] = temp;            }            Console.WriteLine();            for (int i = 0; i < m.Length; i++)            {                Console.WriteLine(i + 1 + ". " + m[i].Surname + ": " + m[i].Sc);            }        }    }}